import styles from './constructor-page.module.css';

import { useCustomization } from "../../context/customization";
import { useState } from 'react';
import ColorsPaletteColumn from '../../components/ui/colors-palette-column/colors-palette-column';
import RectangleCropper from '../../components/rectangle-cropper/rectangle-cropper';

import { Canvas } from '@react-three/fiber';
import Experience from '../experience/experience';
import CupWrapper from '../geometry/cup-wrapper/cup-wrapper';

const ConstructorPage = () => {
  const [menuColor, setMenuColor] = useState(false);
  const [menuBackground, setMenuBackground] = useState(false);
  const [menuLogo, setMenuLogo] = useState(false);
  const {
    cupColor,
    setCupColor,
    wrapperColors,
    front,
    setFront,
    back,
    setBack,
    main,
    setMain,
  } = useCustomization();

  const colorClick = () => {
    if(menuColor) {
      setMenuColor(false);
    } else {
      setMenuLogo(false);
      setMenuBackground(false);
      setMenuColor(true);
    }
  };
  const backgroundClick = () => {
    if(menuBackground) {
      setMenuBackground(false);
    } else {
      setMenuLogo(false);
      setMenuBackground(true);
      setMenuColor(false);
    }
  };
  const logoClick = () => {
    if(menuLogo) {
      setMenuLogo(false);
    } else {
      setMenuLogo(true);
      setMenuBackground(false);
      setMenuColor(false);
    }
  };
  return (

    <main className={styles['main']}>
      <nav className={styles['menu']}>
        <ul className={styles['nav-menu']}>
          <li 
            className={`${styles['nav-menu__item']} 
              ${menuColor ? styles['nav-menu__item_active'] : ''}`
            }
            onClick={colorClick}
          >
            Цвет
          </li>
          <li 
            className={`${styles['nav-menu__item']}
              ${menuBackground ? styles['nav-menu__item_active'] : ''}`
            }
            onClick={backgroundClick}
          >
            Фон
          </li>
          <li 
            className={`${styles['nav-menu__item']}
              ${menuLogo ? styles['nav-menu__item_active'] : ''}`
            }
            onClick={logoClick}
          >
            Логотип
          </li>
        </ul>
      </nav>
      <menu className={`${styles['side-menu']} ${styles['side-menu_left']}`}>
      {(
        menuColor ? 
        <ColorsPaletteColumn 
          colors={wrapperColors}
          setSurface={setCupColor}
          surface={cupColor}
        /> : null
        )}
      </menu>
      <menu className={styles['side-menu']}>
      {(
        menuBackground ? (
        <div className={styles['crop-menu']}>
          <div className={styles['crop-menu__container']}>
            <RectangleCropper 
              ratio={16/9}
              serface={main}
              setSerface={setMain}
            />
          </div>
        </div>
        ): null
      )}
      </menu>
      <section className={styles['main__model']}>
{/*        <div className={styles['block']} style={{background: cupColor.color}}>
          <img src={main.crop ? main.crop : null}
            className={styles['img']}
          />
        </div>
*/}
        <Canvas>
          <color attach='background' args={['#101010']}/>
          <Experience>
            <CupWrapper dimensions={[4, 3, 10]}/>
          </Experience>
        </Canvas>
      </section>
    </main>
  );
};

export default ConstructorPage;
