import { useCustomization } from "../../context/customization";
import styles from './configurator.module.css';

const Configurator = () => {


//  const {lid, setLid, lidColor, setLidColor, cupColor, setCupColor, cupColors, lidColors} = useCustomization();
  const {
    lid,
    setLid,
    lidColor,
    setLidColor,
    lidTexture,
    setLidTexture,
    cupColor,
    setCupColor,
    cupTexture,
    setCupTexture,
    wrapperColors,
    wrapper,
    setWrapper
  } = useCustomization();


  return (
    <div className={styles['configurator']}>
      <div className={styles['configurator__section']}>
        <div className={styles['configurator__section_title']}>
          Конструкция стакана
        </div>
        <div className={styles['configurator__section__values']}>
          <div className={styles['item'] +  styles[`${lid ? 'item--active' : ''}`]} 
            onClick={()=>setLid(true)}
          >
            <div className={styles['item__label']}>
              С крышкой
            </div>
          </div>
          <div className={styles['item'] + styles[`${lid ? '' : 'item--active'}`]} 
            onClick={()=>setLid(false)}
          >
            <div className={styles['item__label']}>
              Без крышки
            </div>
          </div>
        </div>
      </div>
      <div className={styles['configurator__section']}>
        <div className={styles['configurator__section_title']}>
          Цвет стакана
        </div>
        <div className={styles['configurator__section__values']}>
        { wrapperColors.map((item, index) => (
          <div className={styles[`item ${item.color === wrapperColors.color ? 'item--active' : ''}`]}
            onClick={()=>setCupColor(item)}
            key={index}
          >
            <div className={styles['item__dot']} style={{
              backgroundColor: item.color,
            }}/>
            {/*<div className='item__label'>{item.name}</div>*/}
          </div>
        ))}
        </div>
      </div>
    </div>
  );
};

<div className='configurator__section'></div>
export default Configurator;
