import { useCustomization } from "../../context/customization";
import styles from './configurator.module.css';
import { useState } from 'react';
import DropDown from '../ui/dropdown/dropdown';
import ColorsPalette from '../ui/colors-palette/colors-palette';
import DropDownMidle from '../ui/dropdown-midle/dropdown-midle';
import SelectionSwitch from '../ui/selection-switch/selection-switch';


const Configurator = () => {
  const {
    lid,
    setLid,
    lidColor,
    setLidColor,
    lidTexture,
    setLidTexture,
    cupColor,
    setCupColor,
    cupTexture,
    setCupTexture,
    wrapperColors,
    front,
    setFront,
    back,
    setBack,
    main,
    setMain,
  } = useCustomization();

  return (
    <div className={styles['configurator']}>

      <DropDown title={'Main'}>
        <SelectionSwitch 
          surface={main}
          setSurface={setMain}
        />
        <DropDownMidle title={'Цвет'}>
          <ColorsPalette 
            colors={wrapperColors}
            setSurface={setMain}
            surface={main}
          />
        </DropDownMidle>
      </DropDown>
      <DropDown title={'Front'}>
        <SelectionSwitch 
          surface={front}
          setSurface={setFront}
        />
        <DropDownMidle title={'Цвет'}>
          <ColorsPalette 
            colors={wrapperColors}
            setSurface={setFront}
            surface={front}
          />
        </DropDownMidle>
      </DropDown>
      <DropDown title={'Back'}>
        <SelectionSwitch 
          surface={back}
          setSurface={setBack}
        />
        <DropDownMidle title={'Цвет'}>
          <ColorsPalette 
            colors={wrapperColors}
            setSurface={setBack}
            surface={back}
          />
        </DropDownMidle>
      </DropDown>
    </div>
  );
};

<div className='configurator__section'></div>
export default Configurator;
