import { 
      Stage, 
      OrbitControls, 
      MeshReflectorMaterial, 
      boxGeometry, 
      Box,
      Cylinder,
      meshStandardMaterial 
      } from '@react-three/drei';

const Experience = ({ children }) => {

  return (
    <>
     { <Stage
        intensity={1.5}
        environment="city"
        shadows={{
          type: "accumulative",
          color: "#d9afd9",
          colorBlend: 2,
          opacity: 2,
        }}
        adjustCamera={2}  
      >
        <Cylinder 
          args={[tR, bR, h, 50, 50, false, 0*Math.PI, 2*Math.PI]} 
          material-color={'yellow'} 
          visible={true}
        />
        <Cylinder 
          args={[tR*1.01, bR*1.01, h, 50, 50, true, 0*Math.PI, 2*Math.PI]} 
          material-color={'blue'} 
          transparent
          opacity={0.9}
          visible={true}
        />

        <Cylinder 
          args={[tR*1.02, bR*1.02, h, 50, 50, true, 0, 1*Math.PI]} 
          visible={true}
         >
          <meshStandardMaterial 
            color={'red'}
            transparent
            opacity={0.01}

          />
         </Cylinder>

        <Cylinder 
          args={[tR*1.02, bR*1.02, h, 50, 50, true, 1*Math.PI, 1*Math.PI]} 
          material-color={'green'} 
          transparent
          opacity={0.01}
          visible={true}
        />
      </Stage>}
      <OrbitControls
        makeDefault
        minPolarAngle={0}
        maxPolarAngle={Math.PI / 1}
      />
      {<mesh  rotation={[-Math.PI / 2, 0, 0]} position-y={-5.3}>
        <planeGeometry args={[170, 170]} />
        <MeshReflectorMaterial
          blur={[300, 100]}
          resolution={1080}
          mixBlur={1}
          mixStrength={40}
          roughness={1}
          depthScale={0.2}
          minDepthThreshold={0.4}
          maxDepthThreshold={1.4}
          color="#101010"
          metalness={0.5}
        />
      </mesh> }

    </>
  );
};

export default Experience;
