import React, { useRef, useState } from "react";
import styles from './rectangle-cropper.module.css';
import Cropper, { ReactCropperElement } from "react-cropper";
import "cropperjs/dist/cropper.css";

import src from '../../media/1.jpg';

const RectangleCropper= (props) => {

  const {
    ratio,
    serface,
    setSerface
    } = props;

  const cropperRef = useRef(null);

  const onCrop = () => {
    const cropper = cropperRef.current?.cropper;
    setSerface({...serface, crop: cropper.getCroppedCanvas().toDataURL()});
//    console.log(cropper.getCroppedCanvas().toDataURL());
  };
  return (
    <div className={styles['cropper']}>
      <Cropper
        src={serface.image ? serface.image : src}
        // Cropper.js options
        initialAspectRatio={ratio}
        aspectRatio={ratio}
        guides={false}
        crop={onCrop}
        ref={cropperRef}
        style={{width: `100%`, height: `100%`}}
      />
    </div>
  );
};

export default RectangleCropper;
