import styles from './constructor-page.module.css';

import { useCustomization } from "../../context/customization";
import { useState } from 'react';
import ColorsPaletteColumn from '../../components/ui/colors-palette-column/colors-palette-column';
//import ImageCropper from '../ui/image-cropper/image-cropper';

const ConstructorPage = () => {
  const [menuColor, setMenuColor] = useState(false);
  const [menuBackground, setMenuBackground] = useState(false);
  const [menuLogo, setMenuLogo] = useState(false);
  const {
    cupColor,
    setCupColor,
    wrapperColors,
    front,
    setFront,
    back,
    setBack,
    main,
    setMain,
  } = useCustomization();

  const logoClick = () => {
    if(menuLogo) {
      setMenuLogo(false)
    } else {
      setMenuLogo(true)
    }
  };

  return (

    <main className={styles['main']}>
      <nav className={styles['menu']}>
        <ul className={styles['nav-menu']}>
          <li 
            className={`${styles['nav-menu__item']} 
              ${menuColor ? styles['nav-menu__item_active'] : ''}`
            }
            onClick={()=>{return menuColor ? setMenuColor(false) : setMenuColor(true) }}
          >
            Цвет
          </li>
          <li 
            className={`${styles['nav-menu__item']}
              ${menuBackground ? styles['nav-menu__item_active'] : ''}`
            }
            onClick={()=>{return menuBackground ? setMenuBackground(false) : setMenuBackground(true) }}
          >
            Фон
          </li>
          <li 
            className={`${styles['nav-menu__item']}
              ${menuLogo ? styles['nav-menu__item_active'] : ''}`
            }
            onClick={logoClick}
          >
            Логотип
          </li>
        </ul>
      </nav>
      <menu className={`${styles['side-menu']} ${styles['side-menu_left']}`}>
      {(
        menuColor ? 
        <ColorsPaletteColumn 
          colors={wrapperColors}
          setSurface={setCupColor}
          surface={cupColor}
        /> : null
        )}
      </menu>
      <menu className={`${styles['side-menu']} ${styles['side-menu_right']}`}>
        menu2
      </menu>
      <section className={styles['main__model']}>
        <div className={styles['block']}>
          model
        </div>
      </section>
    </main>
  );
};

export default ConstructorPage;
