import React, { useRef, useState } from "react";
import styles from './rectangle-cropper.module.css';
import Cropper, { ReactCropperElement } from "react-cropper";
import "cropperjs/dist/cropper.css";

import image from '../../media/1.jpg';

const RectangleCropper= () => {

  const [src, setSrc] = useState(image);

  const addPicture = (e) => {
    if(!e.target.files[0] && e.target.files.length === 0) {
      alert('inter file');
    } else {
      setSrc(URL.createObjectURL(e.target.files[0]));
    }
  };

  const cropperRef = useRef(null);
  const onCrop = () => {
    const cropper = cropperRef.current?.cropper;
//    console.log(cropper.getCroppedCanvas().toDataURL());
  };
  return (
    <div className={styles['cropper']}>
      <Cropper
        src={src}
        // Cropper.js options
        initialAspectRatio={16 / 9}
        aspectRatio={1 / 1}
        guides={false}
        crop={onCrop}
        ref={cropperRef}
        className={styles['cropper__window']}
      />
    </div>
  );
};

export default RectangleCropper;
